import './App.css';
import React, { useEffect, useState } from 'react'
import { TaskRow } from './components/TaskRows';
import { TaskBanner } from './components/TaskBanner';
import { TaskCreator } from './components/TaskCreator';



function App() {

  const [userName, setUserName] = useState('Ale');

  const [taskItems, setTaskItems] = useState([
    {name:'Tarea', completed: false},
    {name:'Tarea2', completed: false},
    {name:'Tarea3', completed: false},
    {name:'Tarea4', completed: false}
])

const toggleTask = task =>
setTaskItems(taskItems.map(t => (t.name === task.name ? {...t, completed: !t.completed} : t)))

const taskTableRows = () => taskItems.map(task => (
  <TaskRow 
  task={task} 
  key={task.name}
  toggleTask={toggleTask}/>
))


  return (
    <div>
      <TaskCreator/>
      <TaskBanner taskItems={taskItems}/>
      <table>
        <thead>
        <tr>
          <th>Descripci√≥n</th>
          <th>Hecho</th>
        </tr>
        </thead>
        <tbody>
          {taskTableRows()}
        </tbody>
      </table>




    </div>
  );
}

export default App;
